import { Component } from '@angular/core';
import { AlertController } from '@ionic/angular';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {
  name: string = "";
  constructor(private alertController: AlertController) {}

  getVIN() {
    if(this.name.length != 17) {
      console.log("ERROR!");
      this.invalidVINAlert();
    } else {
      console.log(this.name);
    }

  }

  async invalidVINAlert() {
    const alert = await this.alertController.create({
      header: 'Invalid VIN',
      message: 'Please input a valid vin',
      buttons: [{
        text: 'OK',
        handler: () => {
          this.name = "";
        },
      }], 
    });

    await alert.present();
  }

}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>
      Home
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Home</ion-title>
    </ion-toolbar>
  </ion-header>

  <!-- Main Card -->

  <ion-card>
    <img alt="Silhouette of mountains" src="../assets/img/mainbg.jpg" />
    <ion-card-header>
      <ion-card-title>Welcome to CarLookup!</ion-card-title>
    </ion-card-header>
    <ion-card-content>
      Please input your cars VIN in the text field below to find all the information about it!
    </ion-card-content>
  </ion-card>

  <ion-list inset="true">
    <ion-item color="light">
      <ion-text>
        VIN: 
      </ion-text>
      <ion-input size="17" required="true" [(ngModel)]="name" id="input" [clearInput]="true" placeholder="1C3CCBAB6DN642025"></ion-input>
    </ion-item>
    <ion-item (click)="getVIN()" color="light">
      go!
    </ion-item>
  </ion-list>
</ion-content>
